function createDisplay() {
    let display = EPXDisplay.create(DigitalPin.P0, 256, EPXNeoPixelMode.RGB);
    display.setBrightness(30);
    pins.digitalWritePin(DigitalPin.P1, 1);
    return display;
}

function playAnimation(display: EPXDisplay.Strip, animation: Buffer) {
    display.play(animation);
    display.show();
}